"""
Test the fixed FastAPI server locally without running uvicorn
"""
from pyoxigraph import Store, RdfFormat
import json

# Load data
print("Loading data...")
store = Store()
with open("ontology/data_products_ontology.ttl", 'rb') as f:
    store.load(f, RdfFormat.TURTLE)
with open("output/output_data_star.trig", 'rb') as f:
    store.load(f, RdfFormat.TRIG)

print(f"Loaded {len(list(store)):,} quads\n")

# Test query
query = """
PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dct: <http://purl.org/dc/terms/>

SELECT ?dataset ?title ?issued
WHERE {
    ?dataset a dcat:Dataset ;
             dct:title ?title ;
             dct:issued ?issued .
}
LIMIT 5
"""

print("Executing query...")
query_results = store.query(query)

# Get variables BEFORE converting to list
vars = []
if hasattr(query_results, 'variables'):
    var_objects = query_results.variables
    vars = [str(v).lstrip('?') for v in var_objects]
    print(f"✓ Variables: {vars}\n")

# Convert to list
results = list(query_results)
print(f"✓ Got {len(results)} results\n")

# Convert to bindings
bindings = []
for result in results:
    binding = {}
    for var in vars:
        try:
            value = result[var]
            if value is not None:
                value_str = str(value)
                if value_str.startswith('<') and value_str.endswith('>'):
                    binding[var] = {'type': 'uri', 'value': value_str[1:-1]}
                elif value_str.startswith('"'):
                    if '^^' in value_str:
                        parts = value_str.split('^^')
                        binding[var] = {
                            'type': 'literal',
                            'value': parts[0].strip('"'),
                            'datatype': parts[1].strip('<>')
                        }
                    else:
                        binding[var] = {'type': 'literal', 'value': value_str.strip('"')}
                else:
                    binding[var] = {'type': 'literal', 'value': value_str}
        except KeyError:
            continue
    bindings.append(binding)

# Display results
response = {
    'head': {'vars': vars},
    'results': {'bindings': bindings}
}

print("="*80)
print("RESULT:")
print("="*80)
print(json.dumps(response, indent=2))

print("\n" + "="*80)
print("VERIFICATION:")
print("="*80)
for i, binding in enumerate(bindings, 1):
    print(f"\n{i}. Dataset: {binding.get('dataset', {}).get('value', 'N/A')}")
    print(f"   Title: {binding.get('title', {}).get('value', 'N/A')}")
    print(f"   Issued: {binding.get('issued', {}).get('value', 'N/A')}")

print("\n✅ SUCCESS! Bindings are no longer empty!")
